<?php// // ----------------------------------------------------------------------------------------------------// // - Display Errors// // ----------------------------------------------------------------------------------------------------// ini_set('display_errors', 'On');// ini_set('html_errors', 0);// // ----------------------------------------------------------------------------------------------------// // - Error Reporting// // ----------------------------------------------------------------------------------------------------// error_reporting(-1);/*///////////////////////////////////////////////////////////////////////Part of the code from the bookBuilding Findable Websites: Web Standards, SEO, and Beyondby Aarron Walter (aarron@buildingfindablewebsites.com)http://buildingfindablewebsites.comDistrbuted under Creative Commons licensehttp://creativecommons.org/licenses/by-sa/3.0/us////////////////////////////////////////////////////////////////////////*/function storeAddress() {	// Validation	if (empty($_REQUEST['email'])) {		return "No hay ninguna dirección de correo electrónico proporcionada";	}	if(!preg_match("/^[_a-z0-9-]+(\.[_a-z0-9-]+)*@[a-z0-9-]+(\.[a-z0-9-]+)*$/i", $_REQUEST['email'])) {		return "Dirección de correo electrónico inválida";	}	if(strlen($_REQUEST['phone']) != 10){		return "Deben ser 10 dígitos.";	}	// grab an API Key from http://admin.mailchimp.com/account/api/	$api_key = '9ddbbb8a970e8e7f4925463fcba21d0d-us2';	// grab your List's Unique Id by going to http://admin.mailchimp.com/lists/	// Click the "settings" link for the list - the Unique Id is at the bottom of that page.	$list_id = "04be89e761";	require_once('MailChimp.php');	$dopt = (!empty($_REQUEST['dopt']) && $_REQUEST['dopt'] == 'true') ? true : false;	$email = $_REQUEST['email'];	$phone = $_REQUEST['phone'];	$merge_vars = array(		'FNAME' => !empty($_REQUEST['fname']) ? $_REQUEST['fname'] : '',		'PHONE' => !empty($_REQUEST['phone']) ? $_REQUEST['phone'] : '',	);	$api = new MailChimp($api_key);	$valid_key = $api->validateApiKey();	if( !$valid_key ) {		return 'Error: please, check your api-key';	}	$result = $api->call('lists/subscribe', array(        'id'                => $list_id,        'email'             => array('email' => $email),        'merge_vars'        => $merge_vars,        'double_optin'      => $dopt,        'update_existing'   => true,        'replace_interests' => false,        'send_welcome'      => false,    ));	if (!empty($result['email']) && !empty($result['euid']) && !empty($result['leid'])) {		//return '¡Éxito! Revisa tu correo electrónico, en el transcurso del día te llegará un mensaje.';		return 'Success! Check your email to confirm sign up.';	} else {		$message = array();		if( !empty($result['error']) ) {			$message[] = $result['error'];		}		return 'Error: '.implode(' - ', $message);	}}// If being called via ajax, autorun the functionif ( !empty($_REQUEST['ajax']) ) {	echo storeAddress();}?>